    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
// TODO add your handling code here:
        btnentry.setSelected(true);
        btndupdate.setSelected(false);
        pnlentry.setVisible(true);
        pnlupdate.setVisible(false); 
                              try{
                   Class.forName("sun.jdbc.odbc.JdbcOdbcDriver");
                    con=DriverManager.getConnection("jdbc:odbc:rock");
                    st=con.createStatement();                     
                    rs=st.executeQuery("select name from diesease");
         while(rs.next())
          {
            String tname=rs.getString("name");
            cbodiesease.addItem(tname);
          }
         st.close();
        con.close();
        }
          catch(SQLException sq)
         {
              JOptionPane.showMessageDialog(this,"Database Error","Diesease",JOptionPane.ERROR_MESSAGE);   
              this.dispose(); 
         } 
         catch(Exception e)
         {
          JOptionPane.showMessageDialog(this,"There Is Error Occur ","Error",JOptionPane.ERROR_MESSAGE);  
          this.dispose();
         }
        
    }//GEN-LAST:event_formWindowOpened

    private void btnupdateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnupdateActionPerformed
// TODO add your handling code here:
        String m,n;
        m=(String)cbodiesease.getSelectedItem();
        n=txtndiesease.getText();
             
        if(n.equals(""))
        {
         JOptionPane.showMessageDialog(this,"First Enter The Name");
        }
        else
        {
           try
      {
       Class.forName("sun.jdbc.odbc.JdbcOdbcDriver");
       con=DriverManager.getConnection("jdbc:odbc:rock");
       st=con.createStatement();
       int k=st.executeUpdate("update diesease set name='"+n+"' where name='"+m+"'");       
       JOptionPane.showMessageDialog(this,"data saved");
       txtdiesease.setText("");
       st.close();
       con.close();
     }
       catch(SQLException sq)
         {
              JOptionPane.showMessageDialog(this,"Database Error","Diesease",JOptionPane.ERROR_MESSAGE);   
               
         } 
         catch(Exception e)
         {
          JOptionPane.showMessageDialog(this,"There Is Error Occur ","Error",JOptionPane.ERROR_MESSAGE);  
          
         } 
        
        }
    }//GEN-LAST:event_btnupdateActionPerformed

