    private void btndupdateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btndupdateActionPerformed
// TODO add your handling code here:
           int couname=0;
            btnentry.setSelected(false);
        btndupdate.setSelected(true);
        pnlentry.setVisible(false);
        pnlupdate.setVisible(true); 
        
          cbodiesease.removeAllItems();  
           try
        {
          Class.forName("sun.jdbc.odbc.JdbcOdbcDriver");
           con=DriverManager.getConnection("jdbc:odbc:rock");
           st=con.createStatement();                               
          ResultSet rs2=st.executeQuery("select count(name) from diesease");
          while(rs2.next())
          {
              couname=rs2.getInt(1);
          } 
          if(couname>0)
          {    
           ResultSet rs1=st.executeQuery("select name from diesease");
           
           while(rs1.next())
           {
             String tname=rs1.getString("name");
             
             cbodiesease.addItem(tname);
            }
          }
          else
          {
              JOptionPane.showMessageDialog(this,"diesease not available");
              
               btnentry.setSelected(true);
        btndupdate.setSelected(false);
        pnlentry.setVisible(true);
        pnlupdate.setVisible(false);
          }    
          st.close();
          con.close();
         }
          catch(SQLException sq)
         {
              JOptionPane.showMessageDialog(this,"Database Error","Diesease",JOptionPane.ERROR_MESSAGE);   
            
         } 
         catch(Exception e)
         {
          JOptionPane.showMessageDialog(this,"There Is Error Occur ","Error",JOptionPane.ERROR_MESSAGE);  
         
         }
        
    }//GEN-LAST:event_btndupdateActionPerformed

